      "id": 766770,
      "url": "https://remotive.io/remote-jobs/software-dev/senior-frontend-focused-rails-javascript-developer-766770",
      "title": "Senior Frontend focused Rails & JavaScript Developer",
      "company_name": "Ably Realtime",
      "category": "Software Development",
      "tags": [],
      "job_type": "full_time",
      "publication_date": "2021-07-06T01:39:36",
      "candidate_required_location": "UK Only",
